
function goeBURST_algorithm(profileArray, identifiers, type, missings, analysis_method, threshold, callback){
	
	if (type == 'krustal'){
		var algorithm = require('./krustal');
	}
	else{
		var algorithm = require('./prim');
	}

	algorithm(profileArray, identifiers, missings, analysis_method, threshold, function(links, distanceMatrix, profiles, indexToRemove){
        callback(links, distanceMatrix, profiles, indexToRemove);
    });
}

module.exports = goeBURST_algorithm;